import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import Message from "../components/Message";
import { createOrder } from "../actions/orderActions";
import { ORDER_CREATE_RESET } from "../constants/orderConstants";
import { USER_DETAILS_RESET } from "../constants/userConstants";

function PlaceOrderScreen() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const cart = useSelector((state) => state.cart);

  if (!cart.paymentOnAccountMethod) {
    navigate("/payment");
  }

  //   Calculate prices
  const addDecimals = (num) => {
    return (Math.round(num * 100) / 100).toFixed(2);
  };

  const totalPrice = (cart.itemsPrice = addDecimals(
    cart.cartItems.reduce(
      (acc, item) => acc + item.price * item.reservedDays.length,
      0
    )
  ));

  const paymentOnAccount = 0.2 * totalPrice;

  const orderCreate = useSelector((state) => state.orderCreate);
  const { order, success, error } = orderCreate;

  useEffect(() => {
    !cart.paymentOnAccountMethod && navigate("/payment");

    if (success && order) {
      navigate(`/order/${order._id}`);
      dispatch({ type: USER_DETAILS_RESET });
      dispatch({ type: ORDER_CREATE_RESET });
    }
  }, [success, dispatch, navigate, order, cart.paymentOnAccountMethod]);

  const placeOrderHandler = () => {
    dispatch(
      createOrder({
        orderItems: cart.cartItems,
        paymentOnAccountMethod: cart.paymentOnAccountMethod,
        totalPrice,
        paymentOnAccount,
      })
    );
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-8">
          <ul className="list-group">
            <li className="list-group-item">
              <h4>Payment Method</h4>
              <strong>Method: </strong>
              {cart.paymentOnAccountMethod}
            </li>
            <li className="list-group-item">
              <h4>Order Items</h4>
              {cart.cartItems.length === 0 ? (
                <Message>Your cart is empty</Message>
              ) : (
                <ul className="list-group-flush" style={{ padding: "0" }}>
                  {cart.cartItems.map((item) => (
                    <li className="list-group-item" key={item.car}>
                      <div className="row">
                        <div
                          className="col-md-2 col-sm-4 col-8 d-flex align-items-center"
                          style={{ padding: "0" }}
                        >
                          <img
                            src={item.image}
                            className="d-block w-100"
                            alt={item.name}
                          />
                        </div>
                        <div className="col-md-2 col-sm-4 col-4 d-flex align-items-center text-decoration-none">
                          <Link
                            className="text-decoration-none text-cardialred"
                            to={`/cars/${item.car}`}
                          >
                            {item.name}
                          </Link>
                        </div>

                        <div className="col-md-2 col-sm-4 col-12 d-flex align-items-center justify-content-center ">
                          {item.reservedDays.length} x ${item.price} = $
                          {item.reservedDays.length * item.price}
                        </div>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default PlaceOrderScreen;
