import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Message from "../components/Message";
import ModalCalendar from "../components/ModalCalendar";
import { removeFromCart } from "../actions/cartActions";

function Cartscreen() {
  // const [modalShow, setModalShow] = useState(false);
  //   const [id, setId] = useState();

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;

  const removeFromCartHandler = (id) => {
    dispatch(removeFromCart(id));
  };

  const checkoutHandler = () => {
    navigate("/payment");
  };

  const modalHandler = (id) => {
    //setModalShow(true);
    // setId(id);
  };
  return (
    <div className="container">
      <div className="row">
        <div className="col-md">
          <h4>Cart</h4>
          {cartItems.length === 0 ? (
            <Message>
              Your cart is empty <Link to="/cars"> Go Back</Link>
            </Message>
          ) : (
            <>
              <Link to="/cars" className="btn btn-light my-3 mx-0">
                Go Back
              </Link>
              <ul className="list-group list-group">
                {cartItems.map((item) => (
                  <li className="list-group-item" key={item.car}>
                    <div className="row">
                      <div className="col-md-2 col-sm-4 col-8 d-flex align-items-center">
                        <img
                          src={item.image}
                          className="d-block w-100"
                          alt={item.name}
                        />
                      </div>
                      <div className="col-md-2 col-sm-4 d-flex align-items-center text-decoration-none">
                        <Link
                          className="text-decoration-none text-cardialred fs-5"
                          to={`/cars/${item.car}`}
                        >
                          {item.name}
                        </Link>
                      </div>
                      <div className="col-md-2 col-sm-4 col-4 my-2 fw-bold d-flex align-items-center">
                        ${item.price} per day
                      </div>
                      <div className="col-md-2 col-sm-4 mt-1 d-flex align-items-center">
                        <div className="ms-2 my-2">
                          <div className="row mt-2">Reserved days:</div>
                          {item.reservedDays.map((date, index) => (
                            <div className="row" key={index}>
                              {date}
                            </div>
                          ))}
                        </div>
                      </div>
                      <div className="col-md-2 col-sm-4  d-flex align-items-center mt-1">
                        <button
                          className="btn btn-cardialred"
                          variant="primary"
                          data-bs-toggle="modal"
                          data-bs-target="#modalCalendar"
                        >
                          Correct dates
                        </button>
                      </div>
                      <ModalCalendar id={item.car} />

                      <div className="col-md-2 col-sm-4 d-flex align-items-center">
                        <button
                          className="btn"
                          onClick={() => removeFromCartHandler(item.car)}
                        >
                          <i className="bi bi-trash3-fill fs-4"></i>
                        </button>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default Cartscreen;
