import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Message from "../components/Message";
import ModalCalendar from "../components/ModalCalendar";
import { removeFromCart } from "../actions/cartActions";

function Cartscreen() {
  // const [modalShow, setModalShow] = useState(false);
  //   const [id, setId] = useState();

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;

  const removeFromCartHandler = (id) => {
    dispatch(removeFromCart(id));
  };

  const checkoutHandler = () => {
    navigate("/payment");
  };

  // const modalHandler = (id) => {
  //   //setModalShow(true);
  //   // setId(id);
  // };
  return (
    <div className="container-lg">
      <div className="row justify-content-center">
        <div className="col-lg-10">
          <h4>Cart</h4>
          {cartItems.length === 0 ? (
            <Message>
              Your cart is empty <Link to="/cars"> Go Back</Link>
            </Message>
          ) : (
            <>
              <Link to="/cars" className="btn btn-light my-3 mx-0">
                Go Back
              </Link>
              <div className="table-responsive">
                <table className="table table-bordered">
                  <tbody>
                    {cartItems.map((item) => (
                      <tr key={item.car}>
                        <td>
                          <img
                            src={item.image}
                            alt={item.name}
                            style={{ width: "100px" }}
                          />
                        </td>
                        <td>
                          <Link
                            className="text-decoration-none text-cardialred"
                            to={`/cars/${item.car}`}
                          >
                            {item.name}
                          </Link>
                        </td>
                        <td>
                          <strong>${item.price}/day</strong>
                        </td>
                        <td>
                          Reserved days:{" "}
                          {item.reservedDays.map((date, index) => (
                            <span key={index}>{date}</span>
                          ))}
                        </td>
                        <td>
                          <button
                            className="btn btn-sm btn-cardialred "
                            data-bs-toggle="modal"
                            data-bs-target="#modalCalendar"
                          >
                            Correct dates
                          </button>
                        </td>
                        <td>
                          <span
                            className="tt"
                            data-bs-placement="bottom"
                            title="Remove position"
                          >
                            <button
                              className="btn"
                              onClick={() => removeFromCartHandler(item.car)}
                            >
                              <i className="bi bi-trash3-fill fs-5"></i>
                            </button>
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              {/* <ul className="list-group list-group">
                {cartItems.map((item) => (
                  <li className="list-group-item" key={item.car}>
                    <div className="row">
                      <div className="col-2 d-flex align-items-center">
                        <img
                          src={item.image}
                          className="d-block w-100"
                          alt={item.name}
                        />
                      </div>
                      <div className="col-4 col-md-2  d-flex align-items-center text-decoration-none">
                        <Link
                          className="text-decoration-none text-cardialred"
                          to={`/cars/${item.car}`}
                        >
                          {item.name}
                        </Link>
                      </div>
                      <div className="col-2 my-2 px-4 fw-bold d-flex align-items-center">
                        ${item.price}/day
                      </div>
                      <div className="col-4 col-md-2 mt-1 d-flex align-items-center">
                        <div className="ms-2 my-2">
                          <div className="row mt-2">Reserved days:</div>
                          {item.reservedDays.map((date, index) => (
                            <div className="row" key={index}>
                              {date}
                            </div>
                          ))}
                        </div>
                      </div>
                      <div className="col-4 col-md-2 d-flex align-items-center mt-1">
                        <button
                          className="btn btn-sm btn-cardialred "
                          variant="primary"
                          data-bs-toggle="modal"
                          data-bs-target="#modalCalendar"
                        >
                          Correct dates
                        </button>
                      </div>
                      <ModalCalendar id={item.car} />

                      <div className="col-2 d-flex align-items-center justify-content-end ">
                        <span
                          className="tt"
                          data-bs-placement="bottom"
                          title="Remove position"
                        >
                          <button
                            className="btn"
                            onClick={() => removeFromCartHandler(item.car)}
                          >
                            <i className="bi bi-trash3-fill fs-5"></i>
                          </button>
                        </span>
                      </div>
                    </div>
                  </li>
                ))}
              </ul> */}
              <div className="col-md-4">
                <ul className="list-group mt-3">
                  <li className="list-group-item">
                    {" "}
                    <h5>Subtotal</h5>$
                    {cartItems
                      .reduce(
                        (acc, item) =>
                          acc + item.price * item.reservedDays.length,
                        0
                      )
                      .toFixed(2)}
                  </li>
                  <li className="list-group-item">
                    {" "}
                    <h5>Payment on Account</h5>$
                    {cartItems
                      .reduce(
                        (acc, item) =>
                          acc + item.price * item.reservedDays.length,
                        0
                      )
                      .toFixed(2) * 0.2}
                  </li>
                  <li className="list-group-item">
                    <button
                      className="btn btn-cardialred my-3"
                      onClick={checkoutHandler}
                    >
                      Proceed To Checkout
                    </button>
                  </li>
                </ul>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default Cartscreen;
