import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { toast } from "react-toastify";
import { useForm } from "react-hook-form";
import Loader from "../components/Loader";
import { register } from "../actions/userActions";
// import { register, reset } from "../redux/auth/authSlice";
import HeaderContainer from "../themes/HeaderContainer";

function RegisterScreen() {
  //   const [name, setName] = useState("");
  //   const [email, setEmail] = useState("");
  //   const [password, setPassword] = useState("");
  //   const [confirmPassword, setConfirmPassword] = useState("");
  //   const [message, setMessage] = useState(null);

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    password2: "",
  });

  const { name, email, password, password2 } = formData;

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const {
    register: useFormRegister,
    handleSubmit,
    formState: { errors: useFormErrors },
  } = useForm();

  const userRegister = useSelector((state) => state.userRegister);
  let { loading, error, userInfo } = userRegister;

  // const { user, loading, isError, isSuccess, message } = useSelector(
  //   (state) => state.auth
  // );

  useEffect(() => {
    // Redirect when logged in

    userInfo && navigate("/cars");

    isError && toast.error(message);
    // useFormErrors.name?.type === "required" &&
    //   toast.error("Please enter a name");
    // useFormErrors.email?.type === "required" &&
    //   toast.error("Please enter an email");
    // useFormErrors.password?.type === "required" &&
    //   toast.error("Please enter a password");
    // useFormErrors.confirmPassword?.type === "required" &&
    //   toast.error("Please confirm password");
    //message && toast.error("User already exists");
  }, [
    user,
    navigate,
    isError,
    isSuccess,
    // useFormErrors.email,
    // useFormErrors.password,
    // useFormErrors.name,
    // useFormErrors.confirmPassword,
    message,
    dispatch,
  ]);

  const submitHandler = (e) => {
    //e.preventDefault();
    if (password !== password2) {
      toast.error("Passwords do not match");
    } else {
      const userData = {
        name,
        email,
        password,
      };

      dispatch(register(userData));
    }
  };

  return (
    <HeaderContainer>
      <div
        className="container"
        style={{
          height: "40vh",
          marginTop: "20vh",
        }}
      >
        <div className="row justify-content-center">
          <div
            className="col-12 col-md-8 col-lg-6"
            style={{
              border: ".5px solid lightgray",
              borderRadius: "5px",
              padding: "30px",
            }}
          >
            <h4>Sign Up</h4>
            {loading && <Loader />}

            <form onSubmit={handleSubmit(submitHandler)}>
              <label htmlFor="name" className="form-label">
                Name
              </label>
              <div className="input-group mb-3">
                <span className="input-group-text">
                  <i className="bi bi-person-fill text-secondary"></i>
                </span>
                <input
                  {...useFormRegister("name", {
                    required: true,
                  })}
                  type="text"
                  placeholder="Enter a name"
                  className="form-control"
                  name="name"
                  id="name"
                  value={name}
                  onChange={onChange}
                />
              </div>
              <label htmlFor="email" className="form-label">
                Email address
              </label>
              <div className="input-group mb-3">
                <span className="input-group-text">
                  <i className="bi bi-envelope-fill text-secondary"></i>
                </span>
                <input
                  {...useFormRegister("email", {
                    required: true,
                  })}
                  type="email"
                  placeholder="Enter an email"
                  className="form-control"
                  name="email"
                  id="email"
                  value={email}
                  onChange={onChange}
                />
              </div>
              <label htmlFor="password" className="form-label">
                Password
              </label>
              <div className="input-group mb-3">
                <span className="input-group-text">
                  <i className="bi bi-lock-fill text-secondary"></i>
                </span>
                <input
                  {...useFormRegister("password", {
                    required: true,
                  })}
                  type="password"
                  placeholder="Enter password"
                  className="form-control"
                  name="password"
                  id="password"
                  value={password}
                  onChange={onChange}
                />
              </div>
              <label htmlFor="password" className="form-label">
                Confirm Password
              </label>
              <div className="input-group mb-3">
                <span className="input-group-text">
                  <i className="bi bi-lock-fill text-secondary"></i>
                </span>
                <input
                  {...useFormRegister("password2", {
                    required: true,
                  })}
                  type="password"
                  placeholder="Confirm password"
                  className="form-control"
                  name="password2"
                  id="password2"
                  value={password2}
                  onChange={onChange}
                />
              </div>
              <button type="submit" className="btn btn-cardialred mt-3">
                Register
              </button>
            </form>
          </div>
          <div className="my-3 text-center">
            Have an Account?{" "}
            <Link className="text-cardialred " to="/login">
              Login
            </Link>
          </div>
        </div>
      </div>
    </HeaderContainer>
  );
}

export default RegisterScreen;
