import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import Message from "../components/Message";
import Loader from "../components/Loader";
//import ModalCalendar from "../components/ModalCalendar";
import { listCarDetails } from "../actions/carActions";

function CarScreen() {
  const [modalShow, setModalShow] = useState(false);
  // const [modalShow, setModalShow] = useState(false);

  const dispatch = useDispatch();
  const params = useParams();

  const carDetails = useSelector((state) => state.carDetails);
  const { loading, error, car } = carDetails;

  useEffect(() => {
    if (car !== undefined && (!car._id || car._id !== params.id)) {
      dispatch(listCarDetails(params.id));
    }
  }, [dispatch, car, car._id, params.id]);

  return (
    <div className="container">
      <Link className="btn btn-light mb-2" to="/cars">
        Go Back
      </Link>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <>
          {/* <Meta title={car.name} /> */}
          <div className="row">
            <div className="col-md-8">
              <div
                id="carouselExampleFade"
                className="carousel slide carousel-fade"
                data-bs-ride="carousel"
              ></div>
              <div className="carousel-inner">
                {car.images
                  ? car.images.map((image, i) => (
                      <div
                        active={i === 0}
                        className={
                          i === 0 ? "carousel-item active" : "carousel-item"
                        }
                      >
                        <img src={image} className="d-block w-100" alt="..." />
                      </div>
                      // <Carousel.Item key={image}>
                      //   <img
                      //     className="d-block w-100"
                      //     src={image}
                      //     alt="First slide"
                      //   />
                      // </Carousel.Item>
                    ))
                  : null}
                <button
                  className="carousel-control-prev"
                  type="button"
                  data-bs-target="#carouselExampleIndicators"
                  data-bs-slide="prev"
                >
                  <span
                    className="carousel-control-prev-icon"
                    aria-hidden="true"
                  ></span>
                  <span className="visually-hidden">Previous</span>
                </button>
                <button
                  className="carousel-control-next"
                  type="button"
                  data-bs-target="#carouselExampleIndicators"
                  data-bs-slide="next"
                >
                  <span
                    className="carousel-control-next-icon"
                    aria-hidden="true"
                  ></span>
                  <span className="visually-hidden">Next</span>
                </button>
              </div>
            </div>
            {/* <Col md={4}>
              <ListGroup variant="flush">
                <ListGroup.Item className="py-0 mt-3">
                  <h4>{car.name}</h4>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Row className="px-3 py-1">Category: {car.category}</Row>
                  <Row className="px-3 py-1">Fuel: {car.fuel}</Row>
                  <Row className="px-3 py-1">Engine: {car.engine}</Row>
                  <Row className="px-3 py-1">Gearbox: {car.gearbox}</Row>
                  <Row className="px-3 py-1">
                    Acceleration: 0-100kmph {car.acceleration}s
                  </Row>
                  <Row className="px-3 py-1">Price per day: $ {car.price}</Row>
                  <Row className="p-3">
                    <Button
                      type="button"
                      variant="primary"
                      onClick={() => setModalShow(true)}
                    >
                      Reserve
                    </Button>
                    <ModalCalendar
                      id={match.params.id}
                      show={modalShow}
                      onHide={() => setModalShow(false)}
                    />
                  </Row>
                </ListGroup.Item>
              </ListGroup>
            </Col> */}
          </div>
        </>
      )}
    </div>
  );
}

export default CarScreen;
