import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useForm } from "react-hook-form";
import { toast } from "react-toastify";
import Loader from "../components/Loader";
import { login } from "../actions/userActions";
import HeaderContainer from "../themes/HeaderContainer";

function LoginScreen() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitted },
  } = useForm();

  const userLogin = useSelector((state) => state.userLogin);
  const { loading, error, userInfo } = userLogin;

  useEffect(() => {
    if (userInfo) {
      navigate("/cars");
    }
    error && toast.error(error);
  }, [userInfo, navigate, error]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(login(email, password));
  };

  return (
    <HeaderContainer>
      <div
        className="container d-flex flex-column justify-content-center align-items-center"
        style={{
          height: "40vh",
          marginTop: "20vh",
        }}
      >
        <div
          className="col-12 col-md-8 col-lg-6"
          style={{
            border: ".5px solid lightgray",
            borderRadius: "5px",
            padding: "30px",
          }}
        >
          <h4>Sign In</h4>
          {loading && <Loader />}

          <form onSubmit={submitHandler}>
            <label htmlFor="email" className="form-label">
              Email address
            </label>
            <div className="input-group mb-3">
              <span className="input-group-text">
                <i className="bi bi-envelope-fill text-secondary"></i>
              </span>
              <input
                {...register("email", {
                  required: true,
                })}
                type="email"
                className={`form-control ${
                  errors.email ? `is-invalid` : !isSubmitted ? "" : `is-valid`
                }`}
                id=""
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="valid-feedback">Looks good!</div>
            {/* errors */}
            {errors.email?.type === "required" && (
              <div className="invalid-feedback">Please enter an email.</div>
            )}
            <label htmlFor="exampleInputPassword1" className="form-label">
              Password
            </label>
            <div className="input-group mb-3">
              <span className="input-group-text">
                <i className="bi bi-lock-fill text-secondary"></i>
              </span>
              <input
                type="password"
                className="form-control"
                id="exampleInputPassword1"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-cardialred mt-3">
              Submit
            </button>
          </form>
        </div>
        <div className="my-3">
          New Customer?{" "}
          <Link className="text-cardialred" to="/register">
            Register
          </Link>
        </div>
      </div>
      {/* </div> */}
    </HeaderContainer>
  );
}

export default LoginScreen;
