import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { listUsers, deleteUser } from "../actions/userActions";
import { Toast } from "bootstrap";

// const toastBtn = document.getElementById("toastBtn");

// console.log(toastBtn);
// const toastHandler = function () {
//   var toastElList = [].slice.call(document.querySelectorAll(".toast"));
//   var toastList = toastElList.map(function (toastEl) {
//     // Creates an array of toasts (it only initializes them)
//     return new Toast(toastEl); // No need for options; use the default options
//   });
//   toastList.forEach((toast) => toast.show()); // This show them

//   console.log(toastList); // Testing to see if it works
// };

// toastBtn.addEventListener("click", toastHandler());
// document.getElementById("toastBtn").onclick = function () {
//   var toastElList = [].slice.call(document.querySelectorAll(".toast"));
//   var toastList = toastElList.map(function (toastEl) {
//     // Creates an array of toasts (it only initializes them)
//     return new Toast(toastEl); // No need for options; use the default options
//   });
//   toastList.forEach((toast) => toast.show()); // This show them

//   console.log(toastList); // Testing to see if it works
// };

function UserListScreen() {
  const [toast, setToast] = useState(false);
  const toastRef = useRef();

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const userList = useSelector((state) => state.userList);
  const { loading, error, users } = userList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const userDelete = useSelector((state) => state.userDelete);
  const { success: successDelete } = userDelete;

  useEffect(() => {
    var myToast = toastRef.current;
    var bsToast = Toast.getInstance(myToast);
    if (!bsToast) {
      // initialize Toast
      bsToast = new Toast(myToast, { autohide: false });
      // hide after init
      bsToast.hide();
      setToast(false);
    } else {
      // toggle
      toast ? bsToast.show() : bsToast.hide();
    }

    if (userInfo && userInfo.isAdmin) {
      dispatch(listUsers());
    } else {
      navigate("/login");
    }
  }, [dispatch, navigate, successDelete, userInfo, toast]);

  const deleteHandler = (id) => {
    if (window.confirm("Are you sure")) {
      dispatch(deleteUser(id));
    }
  };

  return (
    <div className="container-md">
      {/* <button
        className="btn btn-success"
        onClick={() => setToast((toast) => !toast)}
      >
        Toast {toast ? "hide" : "show"}
      </button> */}

      <h4>Users</h4>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <div className="table-responsive">
          <table
            className="table table-striped table-hover table-bordered align-middle"
            style={{
              textAlign: "center",
            }}
          >
            <thead>
              <tr>
                <th scope="col">ID</th>
                <th scope="col">NAME</th>
                <th scope="col">EMAIL</th>
                <th scope="col">ADMIN</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user._id}>
                  <td>{user._id}</td>
                  <td className="text-nowrap">{user.name}</td>
                  <td>
                    <Link
                      className="text-decoration-none text-cardialred"
                      to={`mailto:${user.email}`}
                    >
                      {user.email}
                    </Link>
                  </td>
                  <td>
                    {user.isAdmin ? (
                      <i
                        className="bi bi-check fs-4"
                        style={{ color: "green" }}
                      ></i>
                    ) : (
                      <i
                        className="bi bi-x fs-4"
                        style={{
                          color: "red",
                        }}
                      ></i>
                    )}
                  </td>
                  <td className="text-nowrap">
                    <button
                      type="button"
                      className="btn text-secondary"
                      onClick={() => navigate(`/admin/user/${user._id}/edit`)}
                    >
                      <i className="bi bi-pencil-square fs-4"></i>
                    </button>
                    <button
                      type="button"
                      className="btn fs-4"
                      // data-bs-dismiss="toast"
                      onClick={() => setToast((toast) => !toast)}
                    >
                      <i
                        className="bi bi-trash3-fill"
                        style={{ color: "#c41e3a" }}
                      ></i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default UserListScreen;
