//import "./App.css";
import React, { useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import HomeScreen from "./screens/HomeScreen";
import ContactScreen from "./screens/ContactScreen";
import ConfirmSendMessage from "./screens/ConfirmSendMessage";

function App() {
  const [cartIsEmpty] = useState(false);

  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<HomeScreen />} />
        <Route
          path="/contact"
          element={
            cartIsEmpty ? (
              <ContactScreen />
            ) : (
              <Navigate to="confirm-send-message" />
            )
          }
        />
        <Route path="/confirm-send-message" element={<ConfirmSendMessage />} />
      </Routes>
    </div>
  );
}

export default App;
