/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from "react";
import { logout, reset } from "../redux/auth/authSlice";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, Link } from "react-router-dom";

function HeaderContainer({ children }) {
  const dispatch = useDispatch();

  const [keyword, setKeyword] = useState("");

  const navigate = useNavigate();

  const { user } = useSelector((state) => state.auth);

  const submitHandler = (e) => {
    e.preventDefault();
    if (keyword.trim()) {
      navigate(`/cars/search/${keyword}`);
    } else {
      navigate("/cars");
    }
  };

  // const userLogin = useSelector((state) => state.userLogin);
  // const { userInfo } = userLogin;

  const logoutHandler = () => {
    dispatch(logout());
    dispatch(reset());
    navigate("/");
  };

  return (
    <div className="container-md">
      <nav className="navbar navbar-expand-lg navbar-dark bg-salmon mb-4 p-2 rounded">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">
            CAR RENTAL
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-5 ms-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className="nav-link" aria-current="page" to="/cart">
                  <i className="bi bi-cart fs-5 me-3"></i>
                </Link>
              </li>
              {user ? (
                <li className="nav-item dropdown">
                  <a
                    className="nav-link dropdown-toggle"
                    id="navbarDropdown"
                    role="button"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                  >
                    {user.name}
                  </a>
                  <ul
                    className="dropdown-menu"
                    aria-labelledby="navbarDropdown"
                  >
                    <li>
                      <Link className="dropdown-item" to="/profile">
                        Profile
                      </Link>
                    </li>

                    <li>
                      <button
                        className="dropdown-item"
                        type="button"
                        onClick={logoutHandler}
                      >
                        Logout
                      </button>
                    </li>
                  </ul>
                </li>
              ) : (
                <li className="nav-item">
                  <Link className="nav-link" aria-current="page" to="/login">
                    <i className="bi bi-person-fill fs-5"></i> Sign In
                  </Link>
                </li>
              )}
            </ul>
            <form className="d-flex" onSubmit={submitHandler}>
              <input
                className="form-control me-2"
                type="search"
                placeholder="Search"
                aria-label="Search"
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)}
              />
              <button className="btn btn-outline-cardialred" type="submit">
                <i className="bi bi-search"></i>
              </button>
            </form>
          </div>
        </div>
      </nav>
      {children}
    </div>
  );
}

export default HeaderContainer;
